                document.addEventListener('DOMContentLoaded', function() {
                    var form1 = document.getElementById('form1');
                    var form2 = document.getElementById('form2');
                    var nextBtn = document.getElementById('nextBtn');
                    var prevBtn = document.getElementById('prevBtn');
                    var submitBtn = document.getElementById('submitBtn');

                    // Handle Next button
                    nextBtn.addEventListener('click', function() {
                        form1.style.display = 'none';
                        form2.style.display = 'block';
                    });

                    submitBtn.addEventListener('click', function(event) {
						event.preventDefault();  // Cegah submit default untuk sementara




						console.log("Data sebelum submit:");



						// Submit form setelah menampilkan log
						document.getElementById('ppdbForm').submit();
					});

                    // Handle Previous button
                    prevBtn.addEventListener('click', function() {
                        form2.style.display = 'none';
                        form1.style.display = 'block';
                    });
                });

div.script:
document.addEventListener('DOMContentLoaded', function () {
    // Add Line button
    var addLineBtn = document.getElementById('add_line');
    var siblingTable = document.getElementById('sibling_table').querySelector('tbody');

    // Function to remove a row
    function removeLine() {
        var row = this.parentElement.parentElement;
        row.remove();
    }

    // Add new line to the table
    addLineBtn.addEventListener('click', function () {
        var newRow = document.createElement('tr');

        // Column 1: Nama Saudara Kandung
        var nameTd = document.createElement('td');
        var nameInput = document.createElement('input');
        nameInput.type = 'text';
        nameInput.name = 'sibling_name[]';  // Store sibling names as an array
        nameInput.className = 'form-control';
        nameTd.appendChild(nameInput);

        // Column 2: Tgl. Lahir
        var birthDateTd = document.createElement('td');
        var birthDateInput = document.createElement('input');
        birthDateInput.type = 'date';
        birthDateInput.name = 'sibling_birthdate[]';  // Store sibling birthdates as an array
        birthDateInput.className = 'form-control';
        birthDateTd.appendChild(birthDateInput);

        // Column 3: Nama Sekolah dan Tingkat
        var schoolTd = document.createElement('td');
        var schoolInput = document.createElement('input');
        schoolInput.type = 'text';
        schoolInput.name = 'sibling_school[]';  // Store sibling schools as an array
        schoolInput.className = 'form-control';
        schoolTd.appendChild(schoolInput);

        // Column 4: Actions (Remove Button)
        var actionsTd = document.createElement('td');
        var removeBtn = document.createElement('button');
        removeBtn.type = 'button';
        removeBtn.className = 'btn btn-danger btn-sm remove-line';
        removeBtn.innerText = 'Remove';
        removeBtn.addEventListener('click', removeLine);  // Attach event to remove button
        actionsTd.appendChild(removeBtn);

        // Append columns to the new row
        newRow.appendChild(nameTd);
        newRow.appendChild(birthDateTd);
        newRow.appendChild(schoolTd);
        newRow.appendChild(actionsTd);

        // Append the new row to the table body
        siblingTable.appendChild(newRow);
    });
});

document.addEventListener('DOMContentLoaded', function () {
    // Tombol "Add Line"
    var addLineBtn = document.getElementById('tambah_tes');
    var siblingTable = document.getElementById('test_table').querySelector('tbody');

    // Fungsi untuk menghapus baris
    function removeLine() {
        var row = this.parentElement.parentElement;
        row.remove();
    }

    // Menambah baris baru ke tabel
    addLineBtn.addEventListener('click', function () {
        var newRow = document.createElement('tr');

        // Kolom 1: Jenis Tes
        var testTypeTd = document.createElement('td');
        var testTypeSelect = document.createElement('select');
        testTypeSelect.name = 'test_type[]';  // Store test types as an array
        testTypeSelect.className = 'form-control';

        // Menambahkan opsi ke dalam select
        var option1 = document.createElement('option');
        option1.value = 'option1_value';
        option1.text = 'Option 1';
        testTypeSelect.appendChild(option1);


        testTypeTd.appendChild(testTypeSelect);


        // Kolom 2: Ya (Radio Button)
        var yesTd = document.createElement('td');
        var yesInput = document.createElement('input');
        yesInput.type = 'radio';
        yesInput.name = `test_status_${siblingTable.children.length}`; // Ensure unique group for each row
        yesInput.value = 'yes';
        yesTd.appendChild(yesInput);

        // Kolom 3: Tidak (Radio Button)
        var noTd = document.createElement('td');
        var noInput = document.createElement('input');
        noInput.type = 'radio';
        noInput.name = `test_status_${siblingTable.children.length}`;  // Ensure unique group for each row
        noInput.value = 'no';
        noTd.appendChild(noInput);

        // Kolom 4: Jika iya, pada usia berapa
        var ageTd = document.createElement('td');
        var ageInput = document.createElement('input');
        ageInput.type = 'number';
        ageInput.name = 'age_when_yes[]';  // Store ages as an array
        ageInput.className = 'form-control';
        ageInput.placeholder = 'Usia';
        ageTd.appendChild(ageInput);

        // Kolom 5: Actions (Remove Button)
        var actionsTd = document.createElement('td');
        var removeBtn = document.createElement('button');
        removeBtn.type = 'button';
        removeBtn.className = 'btn btn-danger btn-sm remove-line';
        removeBtn.innerText = 'Remove';
        removeBtn.addEventListener('click', removeLine);  // Attach event to remove button
        actionsTd.appendChild(removeBtn);

        // Menambahkan kolom ke dalam baris baru
        newRow.appendChild(testTypeTd);
        newRow.appendChild(yesTd);
        newRow.appendChild(noTd);
        newRow.appendChild(ageTd);
        newRow.appendChild(actionsTd);

        // Menambahkan baris baru ke dalam tabel
        siblingTable.appendChild(newRow);
    });
});

document.querySelector('select[name="orang_rumah"]').addEventListener('change', function() {
const sebutkanSection = document.getElementById('sebutkan_section');
if (this.value === 'ya') {
sebutkanSection.style.display = 'block';
} else {
sebutkanSection.style.display = 'none';
}
});
